import React, { useState, useEffect } from "react";
import "./HomePage.css";
import { headers, FoodTable } from "../components/FoodTable.jsx";

import pic1 from "../assets/ÌñáÎ∞ò.jpg";
import pic2 from "../assets/Ïπ¥Î†à.jpg";
import pic3 from "../assets/ÎêúÏ∞å.jpg";
import pic4 from "../assets/ÎßàÎùºÌÉï.jpg";
import pic5 from "../assets/ÌÉïÌõÑÎ£®.jpg";
import ToastModal from "../components/ToastModal/ToastModal";
import { deleteFoodData, getFoodDataAll, getSearchFood } from "../apis/getFoodAPI.js";
import { useNavigate } from "react-router-dom";

const HomePage = ({ user, setRecipeFood }) => {
  const navigate = useNavigate(); // useNavigate ÌõÖ ÏÇ¨Ïö©
  const [items, setItems] = useState([]); // Ï¥àÍ∏∞ ÏïÑÏù¥ÌÖú(ÏãùÌíà) ÏÉÅÌÉúÎ•º Îπà Î∞∞Ïó¥Î°ú ÏÑ§Ï†ï
  const [searchCategory, setSearchCategory] = useState("food_name"); // Í≤ÄÏÉâ Í∏∞Ï§Ä Ï¥àÍ∏∞Í∞í ÏãùÌíàÎ™ÖÏúºÎ°ú ÏÑ§Ï†ï
  const [searchKeyword, setSearchKeyword] = useState(""); // Í≤ÄÏÉâ ÌÇ§ÏõåÎìú ÏÉÅÌÉúÎ•º Îπà Î¨∏ÏûêÏó¥Î°ú ÏÑ§Ï†ï
  const [selectedFoodNames, setSelectedFoodNames] = useState([]);

  const [sortCriteria, setSortCriteria] = useState("expiration_date"); // Ï†ïÎ†¨ Í∏∞Ï§Ä Ï¥àÍ∏∞Í∞í Ïú†ÌÜµÍ∏∞ÌïúÏúºÎ°ú ÏÑ§Ï†ï
  const [sortDirection, setSortDirection] = useState(true); // Ï†ïÎ†¨ Î∞©Ìñ• Ï¥àÍ∏∞Í∞í Ïò§Î¶ÑÏ∞®ÏàúÏúºÎ°ú ÏÑ§

  const [isModalOpen, setModalOpen] = useState(false);
  const changeModal = () => setModalOpen(!isModalOpen);

  // Í≤ÄÏÉâ ÌÇ§ÏõåÎìú Î≥ÄÍ≤Ω Ïãú Ìò∏Ï∂úÎêòÎäî Ìï®Ïàò
  const handleSearchKeywordChange = (e) => {
    setSearchKeyword(e.target.value);
  };

  const handleRecipeSearch = () => {
    setRecipeFood(selectedFoodNames); // ÏÑ†ÌÉùÎêú ÏùåÏãù Ïù¥Î¶ÑÏùÑ ÏÑ§Ï†ï
    navigate("/recipe"); // /recipe ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
  };

  //Ï≤´ Ïã§Ìñâ Ïãú DBÏóêÏÑú Îç∞Ïù¥ÌÑ∞ Î∞õÏïÑÏò§Í∏∞
  const fetchData = async () => {
    try {
      const foodData = await getDataFromDB(user.user_id); // ÏãùÌíà Îç∞Ïù¥ÌÑ∞ Î∞õÏïÑÏò§Í∏∞
      const sortedData = sortItems(foodData, sortCriteria, sortDirection); // Ï¥àÍ∏∞ ÏãùÌíà Îç∞Ïù¥ÌÑ∞ Ï†ïÎ†¨ (Ïú†ÌÜµÍ∏∞Ìïú Í∏∞Ï§Ä, Ïò§Î¶ÑÏ∞®Ïàú)
      setItems(sortedData); // ÏïÑÏù¥ÌÖú ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
    } catch (error) {
      console.error(error);
    }
  };

  // DBÏóêÏÑú Í≤ÄÏÉâÌïú Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò§Í∏∞
  async function searchData(user_id, searchCategory, searchKeyword) {
    console.log(searchKeyword);
    console.log(searchKeyword.trim());
    try {
      const result = await getSearchFood(
        user_id,
        searchCategory,
        searchKeyword.trim()
      );
      setItems(result);
      console.log(result);
      return result;
    } catch (error) {
      console.error(error);
    }
  }

  // DBÏóêÏÑú Ïú†Ï†ÄÏùò Ï†ÑÏ≤¥ ÏùåÏãù Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò§Îäî ÎπÑÎèôÍ∏∞ Ìï®Ïàò
  async function getDataFromDB(user_id) {
    if (user !== null) {
      try {
        const result = await getFoodDataAll(user_id);
        console.log(result);
        return result;
      } catch (error) {
        console.log(error);
      }
    }
  }

  // Ï†ïÎ†¨ Í∏∞Ï§Ä, Î∞©Ìñ•Ïóê ÎßûÍ≤å Ï†ïÎ†¨ÌïòÎäî Ìï®Ïàò
  const sortItems = (items, criteria, direction) => {
    return items.sort((a, b) => {
      if (criteria === "expiration_date") {
        return direction
          ? new Date(a[criteria]) - new Date(b[criteria])
          : new Date(b[criteria]) - new Date(a[criteria]); // Ï†ïÎ†¨ Í∏∞Ï§ÄÏù¥ Ïú†ÌÜµÍ∏∞ÌïúÏù¥Î©¥ ÎÇ†Ïßú ÎπÑÍµê
      } else {
        return direction
          ? a[criteria].localeCompare(b[criteria])
          : b[criteria].localeCompare(a[criteria]); // Ï†ïÎ†¨ Í∏∞Ï§ÄÏù¥ ÏãùÌíàÎ™Ö or Ïπ¥ÌÖåÍ≥†Î¶¨Î©¥ Î¨∏ÏûêÏó¥ ÎπÑÍµê
      }
    });
  };

  // ÏïÑÏù¥ÌÖú ÏÇ≠Ï†ú, ÏÇ≠Ï†ú ÌõÑ ÏïÑÏù¥ÌÖú ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ Í∏∞Îä• Ìï®Ïàò
  const handleDelete = (selectedItems) => {
    const selectedItemIds = selectedItems.map((item) => item.food_id); // ÏÑ†ÌÉùÎêú Ìï≠Î™©Ïùò food_id Î∞∞Ïó¥ ÏÉùÏÑ±
    // ÏÑ†ÌÉùÎêú Ìï≠Î™© Ï†úÏô∏Ìïú ÎÇòÎ®∏ÏßÄ Ìï≠Î™© ÌïÑÌÑ∞ÎßÅ
    setItems(items.filter((item) => !selectedItemIds.includes(item.food_id))); // ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
  };

  const addItem = (newItem) => {
    // ÌòÑÏû¨ ÏãùÌíà Î™©Î°ù Ï§ë Í∞ÄÏû• ÏµúÍ∑ºÏóê Îì±Î°ùÎêú ÏãùÌíàÏùò food_id Ï∞æÍ∏∞
    const latestFoodId =
      items.length > 0 ? Math.max(...items.map((item) => item.food_id)) : 0;
    // ÏÉàÎ°ú Îì±Î°ùÎê† ÏãùÌíàÏùò food_id
    newItem.food_id = latestFoodId + 1;
    // ÏÉàÎ°úÏö¥ ÏïÑÏù¥ÌÖú Ï∂îÍ∞ÄÌïú ÏïÑÏù¥ÌÖú Î∞∞Ïó¥
    let updatedItems = [...items, newItem];
    // Ï†ïÎ†¨ Í∏∞Ï§Ä Í∏∞Î≥∏Í∞íÏóê Îî∞Îùº Îã§Ïãú Ï†ïÎ†¨Ìïú ÏïÑÏù¥ÌÖú Î∞∞Ïó¥
    updatedItems = sortItems(updatedItems, sortCriteria, sortDirection);
    setItems(updatedItems);
  };

  // HomePage Ïª¥Ìè¨ÎÑåÌä∏ Ï≤òÏùå Î†åÎçîÎßÅÎêòÍ≥† ÌôîÎ©¥Ïóê ÌëúÏãúÎê† Îïå Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  useEffect(() => {
    fetchData();
  }, []); // user_id Î≥ÄÍ≤Ω ÏãúÏóêÎßå Îç∞Ïù¥ÌÑ∞ Îã§Ïãú Î°úÎìú?

  return (
    <div className="HomePage">
      <button className="bg-amber-500" onClick={() => setItems(data)}>
        ÏûÑÏãú ÏùåÏãù Ï∂îÍ∞Ä
      </button>
      <button onClick={() => console.log(selectedFoodNames)}>asdf</button>
      <div className="searchSection">
        {/* Í≤ÄÏÉâ Í∏∞Ï§Ä ÏÑ†ÌÉùÌï† Ïàò ÏûàÎäî ÎìúÎ°≠Îã§Ïö¥ */}
        <select
          className="dropdown"
          onChange={(e) => setSearchCategory(e.target.value)}
        >
          <option value="food_name">ÏãùÌíàÎ™Ö</option>
          <option value="category">Ïπ¥ÌÖåÍ≥†Î¶¨</option>
        </select>
        <input
          type="text"
          className="searchBar"
          value={searchKeyword}
          onChange={handleSearchKeywordChange}
          //   onKeyPress={(e) => e.key === "Enter" && searchData(searchCategory, searchKeyword)}
        />
        <button
          type="button"
          className="searchButton"
          onClick={() => {
            searchData(user.user_id, searchCategory, searchKeyword);
          }}
        />
      </div>
      <div className="tableInfo">
        <span> üë§ {user.username} ÎãòÏùò ÎÉâÏû•Í≥† </span>
        <div>
          <button
            type="button"
            className="recipeSearch"
            onClick={() => {
              handleRecipeSearch();
            }}
          >
            Î†àÏãúÌîº Í≤ÄÏÉâ
          </button>
          <button
            type="button"
            className="deleteFood"
            //ÏàòÏ†ï ÌïÑÏöî
            onClick={() => {deleteFoodData(user.user_id, selectedFoodNames);}}
          >
            ÏÇ≠Ï†ú
          </button>
        </div>
      </div>
      <div className="foodTableComponent">
        <div className="scrollableBox">
          <FoodTable
            headers={headers}
            items={items}
            setItems={setItems}
            onDelete={handleDelete}
            user={user}
            sortItems={sortItems}
            sortCriteria={sortCriteria}
            sortDirection={sortDirection}
            setSortCriteria={setSortCriteria}
            setSortDirection={setSortDirection}
            setSelectedFoodNames={setSelectedFoodNames}
          />
        </div>
      </div>
      <div className="addFood">
        <button onClick={changeModal}>+</button>
        <ToastModal
          isOpen={isModalOpen}
          onClose={changeModal}
          addItem={addItem}
          user_id={user.user_id}
        />
      </div>
    </div>
  );
};
export default HomePage;
